/**
 * This will be the local wardrobe that the user can store items and look over them
 */

import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  KeyboardAvoidingView,
  Dimensions,
  Image,
  Keyboard,
  TouchableOpacity,
  TouchableWithoutFeedback,
  Modal,
} from "react-native";
import { Camera } from "expo-camera";
import * as ImagePicker from "expo-image-picker";
import { Avatar, Button, TextInput } from "react-native-paper";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { generateID, uploadImage } from "../../../utils/utils";

const windowWidth = Dimensions.get("window").width;
const windowHeight = Dimensions.get("window").height;

const AddItem = (props) => {
  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [brand, setBrand] = useState("");
  const [cameraOpen, setCameraOpen] = useState(false);
  const [imageURI, setImageURI] = useState();

  const handleSave = async (e) => {
    e.preventDefault();
    Keyboard.dismiss();
    const id = GeneralHelper.getRandomID();
    const username = UserHelper.getUsername(currentUser.email);
    // upload photo to Firebase Storage
    const url = await ImageHelper.uploadImageAsync(imageURI);
    // upload the new product to Firebase Realtime Database
    var product = {
      id: id,
      name: name,
      description: description,
      price: parseFloat(price),
      // favorited_by: [],
      thumbnail_url: url,
      sold_by: username,
      // purchased_by: null,
      created_at: Date.now(),
    };
    db.ref("products")
      .child(id)
      .set(product, (err) => {
        if (err) {
          Alert.alert("Data could not be saved: " + err);
        } else {
          db.ref("users_products").child(username).child("active").push(id);
          Alert.alert("Data saved successfully");
        }
      });
  };

  /**
   * saves the item info in the asych storage
   * @param {*} value the object containing all the item information
   * @param {*} name the name of the item
   */
  const storeItem = async (value, name) => {
    try {
      const jsonValue = JSON.stringify(value);
      await AsyncStorage.setItem(name, jsonValue);
      console.log("just stored " + jsonValue);
    } catch (e) {
      console.log("error in storeData ");
      console.dir(e);
    }
  };

  const labelName = <Text style={{ fontFamily: "Cochin" }}>Name</Text>;
  const labelCategory = <Text style={{ fontFamily: "Cochin" }}>Category</Text>;
  const labelBrand = <Text style={{ fontFamily: "Cochin" }}>Brand</Text>;

  return (
    <KeyboardAvoidingView style={styles.container}>
      <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
        <SafeAreaView style={{ height: "100%", width: "100%" }}>
          <View style={styles.title}>
            <Text style={styles.header}> New Item</Text>
            <TouchableOpacity
              onPress={() => {
                setCameraOpen(true);
              }}
            >
              {imageURI ? (
                <Image
                  source={{
                    uri: imageURI,
                  }}
                  style={styles.photo}
                />
              ) : (
                <Avatar.Icon size={260} icon="camera" />
              )}
            </TouchableOpacity>

            <Modal
              visible={cameraOpen}
              backdropStyle={{ backgroundColor: "transparent" }}
            >
              <CameraView
                closeCamera={() => setCameraOpen(false)}
                setImageURI={setImageURI}
              />
            </Modal>
          </View>
          <View>
            <TextInput
              style={styles.textinput}
              label={labelName}
              onChangeText={(text) => {
                setName(text);
              }}
              value={name}
            />
            <TextInput
              style={styles.textinput}
              label={labelCategory}
              onChangeText={(text) => {
                setCategory(text);
              }}
              value={category}
            />
            <TextInput
              style={styles.textinput}
              label={labelBrand}
              onChangeText={(text) => {
                setBrand(text);
              }}
              value={brand}
            />
          </View>
          <Button
            disabled={!(name && category && brand && imageURI)}
            style={styles.button}
            mode="outlined"
            compact={true}
            // onPress={() => {
            //   const theInfo = { name, category, brand, image: imageURI }; //=== name:name, category:category...
            //   storeItem(theInfo, name);
            //   setName("");
            //   setBrand("");
            //   setCategory("");
            // }}
            onPress={(e) => {
              handleSave(e);
            }}
          >
            <Text style={{ fontFamily: "Cochin", color: "black" }}>Save</Text>
          </Button>
        </SafeAreaView>
      </TouchableWithoutFeedback>
    </KeyboardAvoidingView>
  );
};

function CameraView({ closeCamera, setImageURI }) {
  const [hasPermission, setHasPermission] = useState(null);
  const [cameraRef, setCameraRef] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === "granted");
    })();
  }, []);

  if (hasPermission === null) {
    return <View />;
  }

  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }

  const resetType = () => {
    setType(
      type === Camera.Constants.Type.back
        ? Camera.Constants.Type.front
        : Camera.Constants.Type.back
    );
  };

  const takePhoto = async () => {
    if (cameraRef) {
      let photo = await cameraRef.takePictureAsync();
      setImageURI(photo.uri);
      closeCamera();
    }
  };

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 1,
    });

    if (!result.cancelled) {
      setImageURI(result.uri);
      closeCamera();
    }
  };

  return (
    <SafeAreaView
      style={{
        flex: 1,
        width: windowWidth,
        height: windowHeight,
        backgroundColor: "black",
      }}
    >
      <View style={{ flex: 1 }}>
        <View style={cameraStyles.cameraButtonsContainer}>
          <Text
            style={{ fontSize: 18, marginBottom: 10, color: "white" }}
            onPress={closeCamera}
          >
            Back
          </Text>
          <Avatar.Icon icon="repeat" size={24} onPress={resetType} />
        </View>
      </View>
      <View style={{ height: windowWidth }}>
        <Camera
          style={{ height: windowWidth }}
          type={type}
          ref={(ref) => {
            setCameraRef(ref);
          }}
        />
      </View>
      <View style={{ flex: 1, justifyContent: "flex-end" }}>
        <View style={cameraStyles.cameraButtonsContainer}>
          <Avatar.Icon
            style={{ flex: 1 }}
            icon="image"
            size={30}
            color="white"
