                }}
              >
                <View
                  style={{
                    borderWidth: 2,
                    borderRadius: "50%",
                    borderColor: "white",
                    height: 40,
                    width: 40,
                    backgroundColor: "white",
                  }}
                ></View>
              </View>
            </View>
          </TouchableOpacity>
          <View style={{ flex: 1 }} />
        </View>
      </View>
    </SafeAreaView>
  );
