/**
 * This will be the home screen where people can see snippets of clothings, today's weather etc.
 * Author: Novia Wu
 * Date: 9/29/2021
 */
import React from "react";
import { StyleSheet, View, Text, Button } from "react-native";
import { useNavigation } from "@react-navigation/native";

const Home = () => {
  const navigation = useNavigation();

  return (
    <View style={styles.container}>
      <Text>Home Screen is here, yet to implemented...</Text>
      {/* <View style={styles.items}></View>
      <View style={styles.items}></View> */}
      <View
        style={{ flex: 1, flexDirection: "row", backgroundColor: "yellow" }}
      >
        <View style={{ alignItems: "left", backgroundColor: "yellow" }}>
          <Text style={{ fontSize: 68, display: "block" }}>Quiz 1a</Text>
          <Text style={{ fontSize: 14, justifyContent: screenLeft }}>
            CS153a Fall21
          </Text>
          <Text style={{ fontSize: 14 }}>
            Write the code for this app. Including the title and this text! The
            layout is five units high and three units wide
          </Text>
        </View>
      </View>
      <Button
        title="Add an item"
        onPress={() => {
          navigation.navigate("AddItem");
        }}
      ></Button>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  items: {
    flex: 1,
    flexDirection: "row",
    backgroundColor: "yellow",
  },
});

export default Home;
